import React, { useState, useEffect } from "react";
import "./index.css";
import Dashboard from "./components/Dashboard.js";
import UserManagement from "./components/pages/UserManagement.js";
import RoleManagement from "./components/pages/RoleManagement.js";
import PermissionManagement from "./components/pages/PermissionManagement.js";

function App() {
  const [activeTab, setActiveTab] = useState("Dashboard");
  const [users, setUsers] = useState([]);

  // Render the active component based on the selected tab
  const renderActiveTab = () => {
    switch (activeTab) {
      case "Dashboard":
        return <Dashboard users={users} />;
      case "User Management":
        return <UserManagement users={users} setUsers={setUsers} />;
      case "Role Management":
        return <RoleManagement />;
      case "Permission Management":
        return <PermissionManagement />;
      default:
        return <Dashboard users={users} />;
    }
  };

  return (
    <div className="app-container">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h1 className="sidebar-title">Role Based Access Controller</h1>
        </div>
        <nav className="nav-menu">
          {/* Tab buttons to navigate between components */}
          <button
            className={activeTab === "Dashboard" ? "active" : ""}
            onClick={() => setActiveTab("Dashboard")}
          >
            Dashboard
          </button>
          <button
            className={activeTab === "User Management" ? "active" : ""}
            onClick={() => setActiveTab("User Management")}
          >
            User Management
          </button>
          <button
            className={activeTab === "Role Management" ? "active" : ""}
            onClick={() => setActiveTab("Role Management")}
          >
            Role Management
          </button>
          <button
            className={activeTab === "Permission Management" ? "active" : ""}
            onClick={() => setActiveTab("Permission Management")}
          >
            Permission Management
          </button>
        </nav>
      </aside>
      <main className="content">
        {/* Render the active tab component */}
        {renderActiveTab()}
      </main>
    </div>
  );
}

export default App;
